SELECT * FROM (
SELECT xcqk.task_id, xcqk.cargo_id AS 'cargoId', xcqk.cargoName AS 'cargoName', xcqk.total AS 'total', wcl.finished AS 'finished', wcl.finishedUsedTime , wcl.finishedEfficiency, qcl.clearance, qcl.clearanceUsedTime, qcl.clearanceEfficiency , CASE WHEN round(xcqk.total - wcl.finished, 1) IS NULL THEN xcqk.total ELSE round(xcqk.total - wcl.finished, 1) END AS 'remainder' FROM ( SELECT cabin.task_id, cabin.cargo_id, MAX(cabin.cargo_type) AS 'cargoName' , round(cabin.cargoPreunloading, 1) AS 'total' FROM v_cabin_info cabin GROUP BY cabin.cargo_id, cabin.task_id ) xcqk, ( SELECT SUM(c.OneTask) AS 'clearance' , timestampdiff(SECOND, MIN(c.Time), MAX(c.Time)) / 3600 AS 'clearanceUsedTime' , round(SUM(c.OneTask) / (timestampdiff(SECOND, MIN(c.Time), MAX(c.Time)) / 3600), 2) AS 'clearanceEfficiency' , o.cargo_id, o.task_id FROM ( SELECT cabin.task_id, cabin.cabin_id, cabin.cargo_id , CASE WHEN cabin.end_time IS NULL THEN CURRENT_TIMESTAMP() ELSE cabin.end_time END AS 'end_time', cabin.clear_time FROM v_cabin_info cabin ) o LEFT JOIN ( SELECT `cc`.`cabinId` AS `cabinId`, `bb`.`groupId` AS `groupId`, `bb`.`Cmsid` AS `Cmsid`, `bb`.`Time` AS `Time`, `bb`.`unloaderMove` AS `unloaderMove` , `bb`.`OneTask` AS `OneTask`, `bb`.`operationType` AS `operationType` FROM `tab_temp_c_%s` `cc` JOIN `tab_temp_b_%s` `bb` WHERE `cc`.`id` = `bb`.`groupId` AND `bb`.`operationType` = 1 ) c ON o.clear_time <= c.Time AND o.end_time >= c.Time AND o.cabin_id = c.cabinId GROUP BY o.cargo_id, o.task_id ) qcl, ( SELECT SUM(c.OneTask) AS 'finished' , timestampdiff(SECOND, MIN(c.Time), MAX(c.Time)) / 3600 AS 'finishedUsedTime' , round(SUM(c.OneTask) / (timestampdiff(SECOND, MIN(c.Time), MAX(c.Time)) / 3600), 2) AS 'finishedEfficiency' , o.cargo_id, o.task_id FROM ( SELECT cabin.task_id, cabin.begin_time, cabin.cargo_id, cabin.cabin_id , CASE WHEN cabin.end_time IS NULL AND cabin.clear_time IS NULL THEN CURRENT_TIMESTAMP() WHEN cabin.end_time IS NULL AND cabin.clear_time IS NOT NULL THEN cabin.clear_time WHEN cabin.end_time IS NOT NULL AND cabin.clear_time IS NULL THEN cabin.end_time WHEN cabin.end_time IS NOT NULL AND cabin.clear_time IS NOT NULL THEN cabin.clear_time END AS 'end_time' FROM v_cabin_info cabin ) o LEFT JOIN ( SELECT `cc`.`cabinId` AS `cabinId`, `bb`.`groupId` AS `groupId`, `bb`.`Cmsid` AS `Cmsid`, `bb`.`Time` AS `Time`, `bb`.`unloaderMove` AS `unloaderMove` , `bb`.`OneTask` AS `OneTask`, `bb`.`operationType` AS `operationType` FROM `tab_temp_c_%s` `cc` JOIN `tab_temp_b_%s` `bb` WHERE `cc`.`id` = `bb`.`groupId` AND `bb`.`operationType` = 1 ) c ON o.begin_time <= c.Time AND o.end_time >= c.Time AND o.cabin_id = c.cabinId GROUP BY o.cargo_id, o.task_id ) wcl WHERE xcqk.task_id = qcl.task_id AND xcqk.task_id = wcl.task_id AND xcqk.cargo_id = qcl.cargo_id AND xcqk.cargo_id = wcl.cargo_id
) t where 1=1
